@import './var.scss';
@import './default.scss';


// top start

.top{
    font-size: 18px;
    color: $primary-color;
    padding: 18px 0;

    .icon{
        font-size: 20px;
    }
 
    .center{
        display: flex;
        justify-content: space-between;

        
    }

    &-left{
        display: flex;
        justify-content: flex-start;

        & > div{
            display: flex;
            justify-content: flex-start;
    
            span{
             

                &.icon{
                    margin-right: 5px;
                }
            }

            
        }
        &__email{
            margin-left: 24px;

            &--actual-email{
                text-decoration: underline;
                color:$primary-color;
            }
         }
    }


    &-right{
        display: flex;
        justify-content: flex-start;

        a{
            color: $primary-color;
            font-size: 26px;
            margin-left: 16px;

        }

    }
}


// top ends

// nav starts

nav{
    -webkit-box-shadow: 0px 6px 4px -2px rgba(132,132,132,1);
    -moz-box-shadow: 0px 6px 4px -2px rgba(132,132,132,1);
    box-shadow: 0px 6px 4px -2px rgba(132,132,132,1);
    
    .center{    
        display: flex;
        justify-content: space-between;
        position: relative;
    }

    .logo{
        padding: 10px 0;
        img{
            display: block;
        }
    }

    ul{
        display: flex;
        justify-content: flex-start;

        li{
            list-style: none;
            margin-left: 18px;

            a{
                color: $secondary-color;
                display: block;
                height: 60px;
                line-height: 60px;
                font-size: 18px;

                &:hover{
                    color: $primary-color;
                }
            }

            .active{
                color: $primary-color;
            }
        }
    }

    .down-lists{
        display: none;
    }
}

// nav ends

// banner starts

.banner{
    background: url(../images/banner_1.jpg) center center no-repeat fixed;
    max-width: 100%;
    background-size: cover;

    position: relative;
    z-index: -1;
    .center{
        padding:0 20px;
        height: 500px;
        display: flex;
        align-items: center;
    }
    
    h1{
        max-width: 42%;
        font-size: 51px;
        color: $over-color;
        letter-spacing: 1px;
    }
}

// banner ends

// services start

.services{
    padding: 30px 0 60px 0;
    


   
}


// services ends


// courses start
.courses{
    padding: 30px 0 40px 0;
    background: $secondary-bg-color;
    text-align: center;
    .filter{
        display: inline-block;
        .btn{
            margin-top: 36px;
            background: $over-color;
            // padding: 0;
            width: 200px;

            margin-left: 10px;

            &:hover{
                background: $primary-color;
                color: $over-color;
                cursor: pointer;
            }

         
        }

        .act{
            background: $primary-color;
            color: $over-color;
            margin-left: 0;
            &:hover{

                cursor: default;
            }
        }


    }

    .cards{
        margin-top: 30px;
        >div{
            max-width: 30%;
            background: $over-color;
            padding:0px;

            .for-service-description{
                margin:30px;
                h4{
                    width: 300px;
                    margin:auto;
                    border: 1px solid $primary-color;
                    border-radius: 300px;
                    margin-top: -50px;
                    position: relative;
                    background: $over-color;
                    z-index:100;
                }
            }

            img{
                width:100%;
                height: 350px;
            }
        }
    }
}

// courses ends

// about start

.about{
    padding: 30px 0 40px 0;

    .about-container{
        display: flex;
        justify-content: space-between;

        &__image{
            max-width: 40%;
        }

        &__description{
            max-width: 60%;
            margin-top: 35px;
            font-size: 18px;

            &--counter__container{
                display: flex;

                justify-content: flex-start;
                margin-top:34px;      
                .counter{
                    margin:20px;
                    // display: table-cell;
                    font-size:50px;
                    background-color: $over-color;
                    width:200px;
                    border-radius: 50%;
                    border:8px solid $primary-color;
                    height:200px;
                    // vertical-align: middle;
                    display: flex;
                    align-items: center;
                    justify-content: center;
                }
            }

        }
    }
}


// about ends

@import './media.scss';

sources.styles\\media.scss:
@media(max-width:1300px){

    // for top starts

    .top{
        padding: 16px 20px;
    }

    // top ends

    // nav starts

    nav{
        padding: 0 20px;
    }

    // nav ends

    // services start


    // services ends
    .services, .courses{
        .cards{
            > div{
                max-width: 48%;
            }
        }
    }
}

@media(max-width: 850px){

    // top starts
    .top{
        flex-wrap: wrap;

        &-left{
            flex-wrap: wrap;

   
        }

       
    }

    // top ends

    // for nav starts

    nav{
        ul{
            display: none;
        }

        .down-lists{
            display: block;
            padding: 0 15px;
        }
    }

    .mobile-nav{

        .center{
            flex-direction: column;
        }
        ul{
            display: block;
         
            li:not(:last-child){
                border-bottom: 1px solid $border-color;
            }

        }

      
    }

    // nav ends

    // banner starts

    .banner{
        h1{
            font-size: 38px;
            max-width: 100%;
        }
    }

    // banner ends

    // services start
    .services, .courses{
        .cards{
            flex-direction: column;
            > div{
                max-width: 95%;
            }
        }
    }

    // services ends

    // courses start
    .hide-sell{
        display: none;
    }

    // courses ends

 
}

@media(max-width:620px){

    // top starts

    .top{
        // font-size: 14px;
        &-left{
            &__email{
                margin-left: 0;
            }
        }

       

    }

    // top ends


    // banner starts


    .banner{
        h1{
            text-align: center;
            font-size: 30px;
        }
    }

    // banner ends
}

@media(max-width:360px){

    // top starts

    .top{
        &-right{
            flex-wrap:wrap; 
        }
    }

    // top ends

    // courses start
    .courses{
        .cards{
            > div{
                .for-service-description{
                    h4{
                        font-size: 20px;
                        width: 200px;   
                    }
                }
            }
        }
    }
    // courses ends
}
